{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Horizontal layout of histogram, line chart, and product chart with centered plus and equal signs.",
  "data": {
    "sequence": {
      "start": 1,
      "stop": 1000,
      "step": 1,
      "as": "EventCount"
    }
  },
  "transform": [
    {
      "calculate": "samplePoisson(50)",
      "as": "NumberOfEvents"
    },
    {
      "calculate": "sampleLogNormal(10, 1)",
      "as": "AvgSeverity"
    },
    {
      "calculate": "datum.NumberOfEvents * datum.AvgSeverity",
      "as": "Product"
    }
  ],
  "hconcat": [
    {
      "title": ["Frequency Histogram", "Poisson Distribution (λ=50)"],
      "width": 250,
      "height": 250,
      "mark": "bar",
      "transform": [
        {
          "bin": {"maxbins": 50},
          "field": "NumberOfEvents",
          "as": ["binnedNumberOfEvents", "binnedNumberOfEventsEnd"]
        },
        {
          "aggregate": [
            {"op": "count", "as": "Frequency"}
          ],
          "groupby": ["binnedNumberOfEvents", "binnedNumberOfEventsEnd"]
        },
        {
          "calculate": "datum.Frequency / 1000 / (datum.binnedNumberOfEventsEnd - datum.binnedNumberOfEvents)",
          "as": "Density"
        }
      ],
      "encoding": {
        "x": {
          "field": "binnedNumberOfEvents",
          "type": "quantitative",
          "axis": {"title": "Number of Events"}
        },
        "y": {
          "field": "Density",
          "type": "quantitative",
          "axis": {"title": "Density"}
        },
        "color": {"value": "steelblue"}
      }
    },
    {
      "width": 50,
      "height": 250,
      "mark": {
        "type": "text",
        "align": "center",
        "baseline": "middle",
        "fontSize": 30,
        "text": "+"
      }
    },
    {
      "title": ["Severity Distribution","Lognormal Distribution (μ=2, σ=1)"],
      "width": 250,
      "height": 250,
      "mark": "line",
      "transform": [
        {
          "density": "AvgSeverity",
          "bandwidth": 50000,
          "counts": true
        }
      ],
      "encoding": {
        "x": {
          "field": "value",
          "type": "quantitative",
          "axis": {"title": "Average Severity"}
        },
        "y": {
          "field": "density",
          "type": "quantitative",
          "axis": {"title": "Density"}
        },
        "color": {"value": "steelblue"}
      }
    },
    {
      "width": 50,
      "height": 250,
      "mark": {
        "type": "text",
        "align": "center",
        "baseline": "middle",
        "fontSize": 30,
        "text": "="
      }
    },
    {
      "title": ["Loss Distribution", "Frequency x Avg Severity"],
      "width": 250,
      "height": 250,
      "mark": "line",
      "transform": [
        {
          "density": "Product",
          "bandwidth": 1000000,
          "counts": true
        }
      ],
      "encoding": {
        "x": {
          "field": "value",
          "type": "quantitative",
          "axis": {"title": "Loss"}
        },
        "y": {
          "field": "density",
          "type": "quantitative",
          "axis": {"title": "Density"}
        },
        "color": {"value": "steelblue"}
      }
    }
  ]
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Risk Likelihood Scale Table",
  "width": 350,
  "height": 300,
  "data": {
    "values": [
      {"score": 1, "likelihood": "Negligible", "criteria": "0-5% extremely unlikely\nVirtually impossible (i.e. one off event)"},
      {"score": 2, "likelihood": "Low", "criteria": "6-20% low but not impossible"},
      {"score": 3, "likelihood": "Medium", "criteria": "21-50% fairly likely to occur"},
      {"score": 4, "likelihood": "High", "criteria": "51-80% more likely to occur than not"},
      {"score": 5, "likelihood": "Severe", "criteria": "81-100% almost certainly will occur"}
    ]
  },
  "transform": [
    {"calculate": "datum.score + ' - ' + datum.likelihood", "as": "label"}
  ],
  "vconcat": [
    {
      "layer": [
        {
          "mark": "rect",
          "encoding": {
            "y": {
              "field": "score",
              "type": "ordinal",
              "title": "SCORE",
              "sort": ["1", "2", "3", "4", "5"],
              "axis": {"labelAngle": 0}
            },

            "x": {
              "field": "likelihood",
              "type": "nominal",
              "title": "LIKELIHOOD",
              "sort": ["Negligible", "Low", "Medium", "High", "Severe"]
            },
            "color": {
              "field": "likelihood",
              "type": "nominal",
              "scale": {
                "domain": ["Negligible", "Low", "Medium", "High", "Severe"],
                "range": ["#d0e6f5", "#c4d693", "#f2d14b", "#f39b2c", "#e94e40"]
              },
              "legend": null
            },
            "size": {"value": 200}
          }
        },
        {
          "mark": {
            "type": "text",
            "align": "center",
            "baseline": "middle",
            "dx": 5
          },
          "encoding": {
            "y": {
              "field": "score",
              "type": "ordinal",
              "title": "SCORE",
              "sort": ["1", "2", "3", "4", "5"],
              "axis": {"labelAngle": 0}
            },
            "x": {
              "field": "likelihood",
              "type": "nominal",
              "title": "LIKELIHOOD",
              "sort": ["Negligible", "Low", "Medium", "High", "Severe"]
            },
            "text": {"field": "likelihood", "type": "nominal"},
            "color": {"value": "black"}
          }
        },

        {
      "mark": "text",
      "encoding": {
        "y": {
          "field": "score",
          "type": "ordinal",
          "title": "SCORE",              
          "sort": ["1", "2", "3", "4", "5"]
        },
        "text": {"field": "criteria"},
        "color": {"value": "black"},
        "align": {"value": "left"},
        "x": {"value": 260}
      }
    }
      ]
    } 
  ]
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "A simple bar chart with embedded data.",
  "data": {
    "values": [
      {"KRI": "KRI1", "value": 70},
      {"KRI": "KRI2", "value": 85},
      {"KRI": "KRI3", "value": 60},
      {"KRI": "KRI4", "value": 90}
    ]
  },
  "layer": [
    {
      "mark": {
        "type": "bar",
        "width": 80
      },
      "encoding": {
        "x": {
          "field": "KRI",
          "type": "nominal",
          "axis": {"labelAngle": 0},
          "scale": {"paddingInner": 1, "paddingOuter": 15}
        },
        "y": {"field": "value", "type": "quantitative"},
        "color": {
          "condition": {
            "test": "datum.value > 80",
            "value": "#FF000066"
          },
          "value": "#00497666"
        }
      }
    },
    {
      "mark": {"type": "rule", "color": "red", "size": 1},
      "encoding": {
        "y": {"datum": 80}
      }
    },
    {
      "mark": {
        "type": "text",
        "align": "center",
        "dy": -5,
        "fontWeight": "bold",
        "color": "red"
      },
      "encoding": {
        "x": {"field": "KRI", "type": "nominal"},
        "y": {"field": "value", "type": "quantitative"},
        "text": {
          "condition": {
            "test": "datum.value > 80",
            "value": "Breach"
          }
        }
      }
    }
  ]
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Example of highlighting bars above a marker value",
  "data": {
    "values": [
      {"title":"1. Fraud Detection Rate (5% Threshold)", "subtitle":"%", "range":10, "measure":"Last Month", "value":3, "marker":5},
      {"title":"1. Fraud Detection Rate (5% Threshold)", "subtitle":"%", "range":10, "measure":"This Month", "value":8, "marker":5},
      {"title":"2. Cybersecurity Incident Rate (4/month Threshold)", "subtitle":"incidents/month", "range":10, "measure":"Last Month", "value":3, "marker":4},
      {"title":"2. Cybersecurity Incident Rate (4/month Threshold)", "subtitle":"incidents/month", "range":10, "measure":"This Month", "value":6, "marker":4},
      {"title":"3. Customer Satisfaction Score (>4.7 Target)", "subtitle":"out of 5", "range":5, "measure":"Last Month", "value":4.2, "marker":4.7},
      {"title":"3. Customer Satisfaction Score (>4.7 Target)", "subtitle":"out of 5", "range":5, "measure":"This Month", "value":3.8, "marker":4.7},
      {"title":"4. Credit Risk Exposure ($80Million Threshold)", "subtitle":"Amount", "range":100, "measure":"Last Month", "value":45, "marker":80},
      {"title":"4. Credit Risk Exposure ($80Million Threshold)", "subtitle":"Amount", "range":100, "measure":"This Month", "value":60, "marker":80}
    ]
  },
  "facet": {
    "row": {
      "field": "title",
      "type": "ordinal",
      "header": {
        "labelAngle": 0,
        "title": "",
        "labelAlign": "left",
        "labelPadding": 10
      }
    },
    "column": {
      "field": "measure",
      "type": "ordinal",
      "header": {"labelAngle": 0, "title": ""}
    }
  },
  "spacing": 10,
  "spec": {
    "width": 300,
    "height": 30,
    "transform": [
      {
        "calculate": "datum.value > datum.marker ? datum.value : null",
        "as": "exceeded_value"
      }
    ],
    "layer": [
      {
        "mark": {"type": "bar", "color": "#0049761a"},
        "encoding": {
          "x": {"field": "range", "type": "quantitative", "scale": {"nice": false}, "title": null}
        }
      },
      {
        "mark": {"type": "bar", "color": "#00497666", "size": 12},
        "encoding": {
          "x": {"field": "value", "type": "quantitative"}
        }
      },
      {
        "mark": {"type": "bar", "color": "#e4575588", "size": 10},
        "transform": [
          {"filter": "datum.exceeded_value != null"}
        ],
        "encoding": {
          "x": {"field": "exceeded_value", "type": "quantitative"}
        }
      },
      {
        "mark": {"type": "tick", "color": "darkred", "thickness": 5},
        "encoding": {
          "x": {"field": "marker", "type": "quantitative"}
        }
      },
      {
        "mark": {"type": "text", "align": "right", "baseline": "middle", "dx": 5, "dy": -10, "fontSize": 10},
        "encoding": {
          "x": {"field": "range", "type": "quantitative"},
          "text": {"field": "subtitle", "type": "nominal"}
        }
      }
    ]
  },
  "resolve": {"scale": {"x": "independent"}},
  "config": {"tick": {"thickness": 2}, "scale": {"barBandPaddingInner": 0}}
}

