        "x": {"field": "Value", "title": "Value", "type": "quantitative"},
        "y": {"field": "Density", "title": "Density", "type": "quantitative"}
      }
    },
    {
      "transform": [
        {
          "filter": "datum.Value >= datum.VaR"
        }
      ],
      "mark": {"type": "area", "color": "red", "opacity": 0.3},
      "encoding": {
        "x": {"field": "Value", "type": "quantitative"},
