  ],
  "hconcat": [
    {
      "title": "K-Fold Cross-Validation",
      "transform": [
        {"calculate": "floor((datum.day - 1) / (365 / 5)) + 1", "as": "fold"}
      ],
      "mark": "point",
      "encoding": {
        "x": {"field": "day", "type": "quantitative", "title": "Day"},
