          {"field": "fold", "type": "nominal", "title": "Fold"}
        ]
      }
    },
    {
      "title": "Rolling Window Backtesting",
      "transform": [
        {"calculate": "datum.day >= 51 ? ceil((datum.day - 50) / 73) : null", "as": "window"}
      ],
      "mark": "line",
      "encoding": {
        "x": {"field": "day", "type": "quantitative", "title": "Day"},
        "y": {"field": "value", "type": "quantitative", "title": "Value"},
        "color": {"field": "window", "type": "nominal", "title": "Window"},
        "tooltip": [
          {"field": "day", "type": "quantitative", "title": "Day"},
          {"field": "value", "type": "quantitative", "title": "Value"},
          {"field": "window", "type": "nominal", "title": "Window"}
        ]
      }
    },
    {
      "title": "Expanding Window Backtesting",
      "transform": [
        {"calculate": "datum.day <= 292 ? ceil(datum.day / 73) : null", "as": "window"}
      ],
      "mark": "line",
      "encoding": {
        "x": {"field": "day", "type": "quantitative", "title": "Day"},
