      "title": "Price",
      "scale": {"zero": false}
    },
    "color": {
      "field": "assetInfo",
      "type": "nominal",
      "title": "Asset (μ, σ)"
    },
    "strokeDash": {
      "field": "assetInfo",
      "type": "nominal",
      "title": "Asset (μ, σ)"
    }
  }
}

