  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "hconcat": [
    {
      "width": 300,
      "height": 250,
      "title": "PDFs and Importance Sampling Weights",
      "data": {
        "sequence": {
          "start": -4,
          "stop": 4,
          "step": 0.01,
          "as": "x"
        }
      },
      "transform": [
        {
          "calculate": "densityNormal(datum.x, 0, 1)",
          "as": "Normal"
        },
        {
          "calculate": "densityStudentT(datum.x, 3)",
          "as": "StudentT"
        },
        {
          "calculate": "datum.StudentT / datum.Normal",
          "as": "Weight"
        }
      ],
      "layer": [
        {
          "mark": {"type": "line", "color": "blue"},
          "encoding": {
            "x": {"field": "x", "type": "quantitative", "title": "x"},
            "y": {"field": "StudentT", "type": "quantitative", "title": "Density"}
          }
        },
        {
          "mark": {"type": "line", "color": "red", "opacity": 0.5},
          "encoding": {
            "x": {"field": "x", "type": "quantitative"},
            "y": {"field": "Normal", "type": "quantitative"}
          }
        },
        {
          "mark": {"type": "point", "color": "green", "filled": true},
          "transform": [
            {
              "sample": 100
            }
          ],
          "encoding": {
            "x": {"field": "x", "type": "quantitative"},
            "y": {"field": "Normal", "type": "quantitative"},
            "size": {"field": "Weight", "type": "quantitative", "scale": {"range": [20, 200]}}
          }
        }
      ]
    },
    {
      "width": 300,
      "height": 250,
      "title": "CDFs and Importance Sampling",
      "data": {
        "sequence": {
          "start": -4,
          "stop": 4,
          "step": 0.01,
          "as": "x"
        }
      },
      "transform": [
        {
          "calculate": "cumulativeNormal(datum.x, 0, 1)",
          "as": "NormalCDF"
        },
        {
          "calculate": "cumulativeStudentT(datum.x, 3)",
          "as": "StudentTCDF"
        },
        {
          "calculate": "densityNormal(datum.x, 0, 1)",
          "as": "Normal"
        },
        {
          "calculate": "densityStudentT(datum.x, 3)",
          "as": "StudentT"
        },
        {
          "calculate": "datum.StudentT / datum.Normal",
          "as": "Weight"
        }
      ],
      "layer": [
        {
          "mark": {"type": "line", "color": "blue"},
          "encoding": {
            "x": {"field": "x", "type": "quantitative", "title": "x"},
            "y": {"field": "StudentTCDF", "type": "quantitative", "title": "Cumulative Probability"}
          }
        },
        {
          "mark": {"type": "line", "color": "red", "opacity": 0.5},
          "encoding": {
            "x": {"field": "x", "type": "quantitative"},
            "y": {"field": "NormalCDF", "type": "quantitative"}
          }
        },
        {
          "mark": {"type": "point", "color": "green", "filled": true},
          "transform": [
            {
              "sample": 100
            }
          ],
          "encoding": {
            "x": {"field": "x", "type": "quantitative"},
            "y": {"field": "NormalCDF", "type": "quantitative"},
            "size": {"field": "Weight", "type": "quantitative", "scale": {"range": [20, 200]}}
          }
        }
      ]
    }
  ]
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Economic Forecasting with Sequential Monte Carlo (50 Samples)",
  "width": 500,
  "height": 300,
  "data": {
    "sequence": {
      "start": 0,
      "stop": 200,
      "step": 1,
      "as": "index"
    }
  },
  "transform": [
    {
      "calculate": "floor(datum.index / 50) + 1",
      "as": "quarter"
    },
    {
      "calculate": "datum.index % 50 + 1",
      "as": "particle"
    },
    {
      "calculate": "2.5 + 0.2 * datum.quarter",
      "as": "base_forecast"
    },
    {
      "calculate": "datum.base_forecast + random() * 0.5 - 0.25",
      "as": "gdp_particle"
    }
  ],
  "encoding": {
    "x": {
      "field": "quarter", 
      "type": "ordinal",
      "title": "Quarter",
      "axis": {
        "labelAngle": 360,
        "labelAlign": "right",
        "labelBaseline": "middle"
      }
    }
  },
  "layer": [
    {
      "mark": {"type": "area", "opacity": 0.2, "color": "#004976"},
      "transform": [
        {
          "aggregate": [
            {"op": "q1", "field": "gdp_particle", "as": "lower"},
            {"op": "q3", "field": "gdp_particle", "as": "upper"}
          ],
          "groupby": ["quarter"]
        }
      ],
      "encoding": {
        "y": {"field": "lower", "type": "quantitative", "scale": {"domain": [2, 4]}},
        "y2": {"field": "upper"}
      }
    },
    {
      "mark": {"type": "point", "filled": true, "opacity": 0.3},
      "encoding": {
        "y": {"field": "gdp_particle", "type": "quantitative", "title": "GDP Growth Rate (%)"},
        "color": {"value": "blue"}
      }
    },
    {
      "mark": {"type": "line"},
      "transform": [
        {
          "aggregate": [{"op": "mean", "field": "gdp_particle", "as": "gdp_mean"}],
          "groupby": ["quarter"]
        },
        {
          "calculate": "'SMC Mean'",
          "as": "forecast_type"
        }
      ],
      "encoding": {
        "y": {"field": "gdp_mean", "type": "quantitative"},
        "color": {"field": "forecast_type", "type": "nominal", "scale": {"domain": ["SMC Mean", "Base Forecast"], "range": ["red", "green"]}}
      }
    },
    {
      "mark": {"type": "line"},
      "transform": [
        {
          "aggregate": [{"op": "mean", "field": "base_forecast", "as": "base_mean"}],
          "groupby": ["quarter"]
        },
        {
          "calculate": "'Base Forecast'",
          "as": "forecast_type"
        }
      ],
      "encoding": {
        "y": {"field": "base_mean", "type": "quantitative"},
        "color": {"field": "forecast_type", "type": "nominal", "scale": {"domain": ["SMC Mean", "Base Forecast"], "range": ["red", "green"]}}
      }
    }
  ],
  "title": "GDP Forecasting: Base vs SMC (50 Particles)"
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Generated samples with K-means clustering and Random Forest classification",
  "config": {
    "view": {"stroke": null}
  },
  "data": {
    "sequence": {
      "start": 0,
      "stop": 300,
      "step": 1,
      "as": "index"
    }
  },
  "transform": [
    {
      "calculate": "datum.index % 3", 
      "as": "trueCluster"
    },
    {
      "calculate": "datum.trueCluster == 0 ? sampleLogNormal(0.3,0.5) * 50 : (datum.trueCluster == 1 ? sampleLogNormal(0.1,1) * 10 + 15 : sampleLogNormal(0.2,0.4) * 20 + 30)", 
      "as": "x"
    },
    {
      "calculate": "datum.trueCluster == 0 ? sampleLogNormal(0.1,0.2) * 10 + 5 : (datum.trueCluster == 1 ? sampleLogNormal(0.2,0.2) * 10 + 20 : sampleLogNormal(0.1,0.3) * 10 + 10)", 
      "as": "y"
    },
    {
      "calculate": "datum.x < 30 ? 0 : (datum.x < 60 ? 1 : 2)",
      "as": "kMeansCluster"
    },
    {
      "calculate": "datum.x + datum.y < 50 ? 0 : (datum.x + datum.y < 100 ? 1 : 2)",
      "as": "rfClass"
    }
  ],
  "hconcat": [
    {
      "title": "True Clusters",
      "mark": "point",
      "encoding": {
        "x": {"field": "x", "type": "quantitative", "axis": {"title": "X-axis"}},
        "y": {"field": "y", "type": "quantitative", "axis": {"title": "Y-axis"}},
        "color": {"field": "trueCluster", "type": "nominal", "legend": {"title": "True Cluster"}}
      }
    },
    {
      "title": "K-means Clustering",
      "mark": "point",
      "encoding": {
        "x": {"field": "x", "type": "quantitative", "axis": {"title": "X-axis"}},
        "y": {"field": "y", "type": "quantitative", "axis": {"title": "Y-axis"}},
        "color": {"field": "kMeansCluster", "type": "nominal", "legend": {"title": "K-means Cluster"}}
      }
    },
    {
      "title": "Random Forest Classification",
      "mark": "point",
      "encoding": {
        "x": {"field": "x", "type": "quantitative", "axis": {"title": "X-axis"}},
        "y": {"field": "y", "type": "quantitative", "axis": {"title": "Y-axis"}},
        "color": {"field": "rfClass", "type": "nominal", "legend": {"title": "RF Class"}}
      }
    }
  ]
}

